import React, { Component } from 'react'
import ListItemCard from './ListItemCard'
import AddItem from './AddItem.png'

const Sorting = {
    TASK_INCREASING: "TASK_INCREASING",
    TASK_DECREASING: "TASK_DECREASING",
    DUE_DATE_INCREASING: "DUE_DATE_INCREASING",
    DUE_DATE_DECREASING: "DUE_DATE_DECREASING",
    STATUS_INCREASING: "STATUS_INCREASING",
    STATUS_DECREASING: "STATUS_DECREASING"
  }

export class ListItemsTable extends Component {

    render() {
        return (
            <div id="list_items_container">
                <div className="list_item_header_card">
                    <div className="list_item_task_header" onClick={this.props.sortByTask.bind(this)}>Task</div>
                    <div className="list_item_due_date_header" onClick={this.props.sortByDueDate.bind(this)}>Due Date</div>
                    <div className="list_item_status_header" onClick={this.props.sortByStatus.bind(this)}>Status</div>
                </div>
                {
                    this.props.todoList.items.map((todoItem)=>(
                        <ListItemCard 
                            key={todoItem.key}
                            listItem={todoItem}
                            todoList={this.props.todoList}
                            loadList={this.props.loadList} 
                            goItemScreen={this.props.goItemScreen.bind(this, todoItem)} />
                    ))
                }
                <div 
                    className="list_item_add_card"
                    onClick={this.props.goItemScreen.bind(this, {
                        "key": this.props.todoList.items.length,
                        "description": null,
                        "due_date": null,
                        "assigned_to": null,
                        "completed": false
                    })}
                >
                    <img src={AddItem} />
                </div>
            </div>
        )
    }
}

export default ListItemsTable
