import React, { Component } from 'react';
import testTodoListData from './TestTodoListData.json'
import HomeScreen from './components/home_screen/HomeScreen'
import ItemScreen from './components/item_screen/ItemScreen'
import ListScreen from './components/list_screen/ListScreen'
import { runInThisContext } from 'vm';
import { updateExpression } from '@babel/types';

const AppScreen = {
  HOME_SCREEN: "HOME_SCREEN",
  LIST_SCREEN: "LIST_SCREEN",
  ITEM_SCREEN: "ITEM_SCREEN"
}

class App extends Component {
  state = {
    currentScreen: AppScreen.HOME_SCREEN,
    todoLists: testTodoListData.todoLists,
    currentList: null,
    currentItem: null
  }

  goHome = () => {
    this.setState({currentScreen: AppScreen.HOME_SCREEN});
    this.setState({currentList: null});
    this.setState({currentItem: null});
  }

  loadList = (todoListToLoad) => {
    this.setState({currentScreen: AppScreen.LIST_SCREEN});
    this.setState({currentList: todoListToLoad});
    this.setState({currentItem: null});
    console.log("currentList: " + this.state.currentList);
    console.log("currentScreen: " + this.state.currentScreen);
  }

  goItemScreen = (itemToLoad) => {
    this.setState({currentScreen: AppScreen.ITEM_SCREEN});
    this.setState({currentItem: itemToLoad});
  }

  processEditItem() {
    this.setState({
      todoLists: {
        ...this.state.todoLists, [this.state.todoLists[this.state.currentItem.key]]: this.state.currentItem
      }});
    this.loadList(this.state.currentList);
  }
  processSubmitNewItem() {
    this.setState({currentList: [...this.state.currentList, this.state.currentItem]});
    this.loadList(this.state.currentList);
  }

  render() {
    switch(this.state.currentScreen) {
      case AppScreen.HOME_SCREEN:
        return <HomeScreen 
        loadList={this.loadList.bind(this)}
        todoLists={this.state.todoLists} />;
      case AppScreen.LIST_SCREEN:            
        return <ListScreen
        goHome={this.goHome.bind(this)}
        todoList={this.state.currentList}
        loadList={this.loadList.bind(this)} 
        goItemScreen={this.goItemScreen.bind(this)} />;
      case AppScreen.ITEM_SCREEN:
        return <ItemScreen
        currentScreen={this.state.currentScreen}
        loadList={this.loadList.bind(this)}
        todoList={this.state.currentList} 
        todoItem={this.state.currentItem}
        processEditItem={this.processEditItem.bind(this)}
        processSubmitNewItem={this.processSubmitNewItem.bind(this)}/>;
      default:
        return <div>ERROR</div>;
    }
  }
}

export default App;
